INSERT INTO public.products (title, description, images, tag, fav, views, sort_order, created_at)
VALUES 
  ('热中症预警仪', '主要功能：实时监测环境温湿度，根据算法显示7种状态（如危险、严重警告、舒适等）\n\n应用场景：放置在没有空调的老人家中、建筑工地休息区、学校体育馆或户外露营地，帮助用户及时补水或降温，防止中暑意外。', '[]'::jsonb, '默认', 0, 0, 10, extract(epoch from now()) * 1000 + 1000),
  ('涂鸦WIFI智能气象钟', '主要功能：通过WIFI连接涂鸦APP，自动获取网络标准时间及当地未来天气预报数据，同步显示室外温湿度。\n\n应用场景：放置在客厅或玄关，用户出门前看一眼即可得知今日气温和是否下雨，方便穿衣带伞，无需查看手机。', '[]'::jsonb, '默认', 0, 0, 20, extract(epoch from now()) * 1000 + 2000),
  ('蓝牙自动对时闹钟', '主要功能：无需手动设置，手机蓝牙连接后自动同步时间，支持多国语言星期显示。\n\n应用场景：酒店客房、学生宿舍或老人卧室，解决传统闹钟时间越走越慢或断电后需要重新复杂设置的痛点。', '[]'::jsonb, '默认', 0, 0, 30, extract(epoch from now()) * 1000 + 3000),
  ('RGB幻彩电竞时钟', '主要功能：专为电竞房设计，带有流光溢彩的RGB氛围灯，支持触摸变色和声控唤醒。\n\n应用场景：搭配电脑桌面的电竞房（Gaming Setup）、游戏主播的背景装饰，或者作为年轻人的床头潮流摆件。', '[]'::jsonb, '默认', 0, 0, 40, extract(epoch from now()) * 1000 + 4000),
  ('高精度食品盐度计', '主要功能：用于厨房汤汁盐度检测或水族馆海水盐度（PPT）检测，具有高分辨率。\n\n应用场景：高血压人群控制饮食盐分摄入、餐厅后厨标准化汤底制作、以及海水鱼缸的盐度配置。', '[]'::jsonb, '默认', 0, 0, 50, extract(epoch from now()) * 1000 + 5000),
  ('自行车码表', '主要功能：显示当前骑行速度、平均速度、总里程、卡路里消耗以及车辆保养提醒。\n\n应用场景：户外骑行运动、通勤代步，帮助骑行者掌握运动强度和车辆保养周期。', '[]'::jsonb, '默认', 0, 0, 60, extract(epoch from now()) * 1000 + 6000),
  ('速读食品温度探针', '主要功能：双键操作，0.5秒极速测温，量程覆盖负30度至280度（油炸烘焙均可），防水设计。\n\n应用场景：油炸食品时控制油温、冲泡婴儿奶粉测温、制作巧克力或牛排煎烤时快速确认中心温度。', '[]'::jsonb, '默认', 0, 0, 70, extract(epoch from now()) * 1000 + 7000),
  ('超大屏电波时钟', '主要功能：超大字号显示时间与温湿度，支持BPC/WWVB电波自动对时，适合老人或会议室使用。\n\n应用场景：挂在大型会议室、学校教室或视力不佳的老人房间，确保时间绝对精准且易于远距离读取。', '[]'::jsonb, '默认', 0, 0, 80, extract(epoch from now()) * 1000 + 8000),
  ('室内水培种植机', '主要功能：在室内模拟太阳光和水循环，自动种植蔬菜或香草，集成营养液浓度监测提示缺水或缺肥。\n\n应用场景：城市公寓的厨房台面或办公室桌面，用于种植新鲜香草（罗勒、薄荷）或作为科普教育设备。', '[]'::jsonb, '默认', 0, 0, 90, extract(epoch from now()) * 1000 + 9000),
  ('意式咖啡电子秤', '主要功能：同时显示重量和计时，用于手冲咖啡或意式浓缩，包含倒计时与正计时功能。\n\n应用场景：咖啡爱好者制作手冲咖啡时精确控制粉水比和萃取时间，或配合意式咖啡机使用。', '[]'::jsonb, '默认', 0, 0, 100, extract(epoch from now()) * 1000 + 10000),
  ('家用气象站', '主要功能：由室内主机和室外传感器组成，显示室内外温湿度、气压趋势和天气预报，并利用电波信号自动校准时间。\n\n应用场景：关注天气的家庭，传感器挂在院子或阳台，主机放客厅，实时监控室外温差，指导园艺种植或出行穿衣。', '[]'::jsonb, '默认', 0, 0, 110, extract(epoch from now()) * 1000 + 11000),
  ('智能体脂秤', '主要功能：测量体重、体脂率、水分、肌肉量等身体数据，并通过蓝牙同步到手机APP。\n\n应用场景：家庭浴室或健身房，用户每天早上监测身体指标变化，辅助减肥或增肌计划。', '[]'::jsonb, '默认', 0, 0, 120, extract(epoch from now()) * 1000 + 12000),
  ('自动逗猫激光塔', '主要功能：放置在地板上，自动控制激光点上下左右随机移动，供宠物抓扑，带定时关闭功能。\n\n应用场景：主人上班不在家时，放在地板上自动运行，帮助宠物猫消耗精力，防止拆家。', '[]'::jsonb, '默认', 0, 0, 130, extract(epoch from now()) * 1000 + 13000),
  ('橱柜雷达感应灯', '主要功能：安装在橱柜或衣柜下方，检测到人靠近自动亮起，离开后自动熄灭，支持亮度调节。\n\n应用场景：厨房吊柜下方切菜补光、衣柜内部找衣服照明、夜间起床的床边地脚灯。', '[]'::jsonb, '默认', 0, 0, 140, extract(epoch from now()) * 1000 + 14000),
  ('蓝牙防丢寻找器', '主要功能：挂在钥匙或钱包上，手机APP点击可让其发出蜂鸣声，或双击设备寻找手机。\n\n应用场景：挂在容易乱放的钥匙串、钱包、宠物项圈或老人拐杖上，解决“出门找不到东西”的焦虑。', '[]'::jsonb, '默认', 0, 0, 150, extract(epoch from now()) * 1000 + 15000),
  ('自然光唤醒闹钟', '主要功能：在设定的闹钟时间前30分钟开始逐渐变亮模拟日出，配合鸟叫或收音机唤醒用户。\n\n应用场景：放在床头柜，帮助在此黑暗环境（如冬季早晨）中起床困难的人群通过光线自然苏醒。', '[]'::jsonb, '默认', 0, 0, 160, extract(epoch from now()) * 1000 + 16000),
  ('智能花园浇水定时器', '主要功能：连接户外水龙头，通过手机远程控制开关水或设定定时浇灌计划。\n\n应用场景：别墅花园或大阳台，连接滴灌系统，主人外出旅游时也能定时给花草浇水。', '[]'::jsonb, '默认', 0, 0, 170, extract(epoch from now()) * 1000 + 17000),
  ('手持激光测距仪', '主要功能：装修或看房时使用，一键测量距离、面积和体积，屏幕显示数据。\n\n应用场景：室内设计师量房、房产中介带看、家庭装修购买家具前测量尺寸。', '[]'::jsonb, '默认', 0, 0, 180, extract(epoch from now()) * 1000 + 18000),
  ('无线蓝牙K歌麦克风', '主要功能：集成了麦克风和蓝牙音箱，连接手机即可唱歌，带有混响和变声效果。\n\n应用场景：家庭聚会、车载娱乐、儿童练歌，随时随地连接手机K歌软件进行娱乐。', '[]'::jsonb, '默认', 0, 0, 190, extract(epoch from now()) * 1000 + 19000),
  ('自动宠物喂食机', '主要功能：设置定时定量自动投放猫粮，支持录制主人的语音呼唤宠物吃饭。\n\n应用场景：主人短期出差或加班晚归时，确保家里的猫狗能按时吃上饭。', '[]'::jsonb, '默认', 0, 0, 200, extract(epoch from now()) * 1000 + 20000),
  ('数字厨房电子秤', '主要功能：高精度称重，多种单位切换，自动关机。\n\n应用场景：烘焙称量面粉黄油、健身人群称量食物热量、日常烹饪配料称重。', '[]'::jsonb, '默认', 0, 0, 210, extract(epoch from now()) * 1000 + 21000),
  ('智能驼背矫正器', '主要功能：佩戴在背部，当检测到用户驼背角度超过设定值时，发出震动提醒。\n\n应用场景：学生写作业、白领办公时佩戴，通过实时震动反馈帮助养成挺胸抬头的习惯。', '[]'::jsonb, '默认', 0, 0, 220, extract(epoch from now()) * 1000 + 22000),
  ('充电式暖手宝', '主要功能：双面发热暖手，可调节温度档位，同时可作为充电宝给手机充电。\n\n应用场景：冬季户外通勤、寒冷教室内暖手、女生生理期热敷腹部。', '[]'::jsonb, '默认', 0, 0, 230, extract(epoch from now()) * 1000 + 23000),
  ('车载蓝牙FM发射器', '主要功能：插入汽车点烟器，连接手机蓝牙，通过FM频段将手机音乐发射到汽车音响。\n\n应用场景：老款汽车没有蓝牙功能时，通过此设备将手机音乐或导航声音传输到汽车音响播放。', '[]'::jsonb, '默认', 0, 0, 240, extract(epoch from now()) * 1000 + 24000),
  ('木材/墙体湿度检测仪', '主要功能：两根探针插入木头或墙壁，通过电阻变化显示含水率，防止装修发霉。\n\n应用场景：木材加工厂检测木料干燥度、房屋验收时检测墙壁是否漏水受潮、防止地板铺设后变形。', '[]'::jsonb, '默认', 0, 0, 250, extract(epoch from now()) * 1000 + 25000),
  ('数字角度尺', '主要功能：带有磁性底座，吸附在锯片或管道上，显示相对角度和水平度。\n\n应用场景：木工切割锯片角度校准、管道安装找平、建筑施工角度测量。', '[]'::jsonb, '默认', 0, 0, 260, extract(epoch from now()) * 1000 + 26000),
  ('便携式冲牙器', '主要功能：高压脉冲水流清洁牙缝，多档位调节。\n\n应用场景：饭后清洁牙缝食物残渣、正畸人群清洁牙套盲区、出差旅行携带。', '[]'::jsonb, '默认', 0, 0, 270, extract(epoch from now()) * 1000 + 27000),
  ('户外露营灯', '主要功能：高亮度照明，支持SOS闪烁，内置大电池可反向充电。\n\n应用场景：户外露营野餐照明、夜间钓鱼、车内应急检修或作为停电时的应急光源。', '[]'::jsonb, '默认', 0, 0, 280, extract(epoch from now()) * 1000 + 28000),
  ('数字胎压计', '主要功能：连接轮胎气嘴，快速显示胎压数值，带夜光灯。\n\n应用场景：汽车日常保养检查、长途出行为轮胎测压，确保行车安全。', '[]'::jsonb, '默认', 0, 0, 290, extract(epoch from now()) * 1000 + 29000),
  ('电动红酒开瓶器', '主要功能：按键自动旋入并拔出红酒木塞，带切箔刀。\n\n应用场景：家庭聚餐、红酒品鉴会、宴席，解决传统开瓶器费力且容易断塞的问题。', '[]'::jsonb, '默认', 0, 0, 300, extract(epoch from now()) * 1000 + 30000),
  ('指纹挂锁', '主要功能：用于健身房储物柜或背包，指纹解锁，USB充电。\n\n应用场景：健身房存包、学生宿舍柜子、旅行箱防盗，解决忘带钥匙或忘记密码的烦恼。', '[]'::jsonb, '默认', 0, 0, 310, extract(epoch from now()) * 1000 + 31000),
  ('白噪音助眠仪', '主要功能：放置在床头，播放雨声、风扇声等自然音效辅助睡眠，带有RGB夜灯和定时关闭功能。\n\n应用场景：失眠人群助眠、遮盖环境噪音（如邻居装修声）、安抚哭闹的婴儿入睡。', '[]'::jsonb, '默认', 0, 0, 320, extract(epoch from now()) * 1000 + 32000),
  ('智能提醒药盒', '主要功能：具备多个独立药仓，用户设定服药计划后，到点声光提醒并自动弹开对应药仓，同时通过APP记录服药记录，若未服药则推送到子女手机。\n\n应用场景：\n• 患有慢性病需要每日按时服药的老年人\n• 需要服用多种维生素或补剂的健身人群\n• 记忆力减退，容易忘记是否已经吃过药的用户', '[]'::jsonb, '默认', 0, 0, 330, extract(epoch from now()) * 1000 + 33000),
  ('智能压力坐垫', '主要功能：在坐垫中植入压力传感器阵列，监测用户的坐姿是否端正、久坐时间是否过长，通过APP生成坐姿热力图并进行震动提醒。\n\n应用场景：\n• 程序员或办公室白领，预防腰椎间盘突出\n• 司机长途驾驶时的疲劳监测与坐姿调整', '[]'::jsonb, '默认', 0, 0, 340, extract(epoch from now()) * 1000 + 34000),
  ('智能数字土壤检测仪', '主要功能：插入土壤中，实时通过LCD屏幕显示土壤的水分百分比、酸碱度以及环境光照强度。\n\n应用场景：\n• 家庭园艺爱好者判断盆栽是否需要浇水\n• 种植绣球花或蓝莓等特定植物时调节酸碱度\n• 检查室内植物摆放位置的光照条件是否充足', '[]'::jsonb, '默认', 0, 0, 350, extract(epoch from now()) * 1000 + 35000),
  ('家用空气质量检测仪 (IAQ Monitor)', '主要功能：集成多合一传感器，实时监测室内温度、湿度、PM2.5、甲醛（HCHO）或TVOC数值，通过红/黄/绿三色LED指示灯直观反馈空气优良等级。\n\n应用场景：\n• 婴儿房或老人房间的全天候空气环境监控\n• 与空气净化器联动，当空气变差时自动净化。', '[]'::jsonb, '默认', 0, 0, 360, extract(epoch from now()) * 1000 + 36000);
